mapper.update:
UPDATE t_board
SET viewCount = viewCount + 1
WHERE bno = #{bno}

UPDATE t_board
SET title = #{title},
    content = #{content}
WHERE bno = #{bno}
  AND passwd = #{passwd}

mapper.delete:
DELETE FROM t_board
WHERE bno = #{bno}
  AND passwd = #{passwd}

mapper.select:
SELECT COUNT(*)
FROM t_board
WHERE bno = #{bno}
  AND passwd = #{passwd}

update.foreach:
UPDATE t_board
SET is_private = CASE WHEN is_private = 'Y' THEN 'N' ELSE 'Y' END
WHERE bno = #{bno}
