mapper.select:
SELECT

FROM t_board b
WHERE 1=1



        ORDER BY ${orderBy}


        ORDER BY b.bno DESC


LIMIT #{startRow}, #{pageSize}

SELECT

FROM t_board b
WHERE b.bno = #{bno}

mapper.update:
UPDATE t_board
SET view_count = view_count + 1
WHERE bno = #{bno}

UPDATE t_board
SET title = #{title},
    content = #{content}
WHERE bno = #{bno}
  AND passwd = #{passwd}

mapper.select:
SELECT COUNT(*) > 0
FROM t_board
WHERE bno = #{bno}
  AND passwd = #{passwd}

mapper.delete:
DELETE FROM t_board
WHERE bno = #{bno}
  AND passwd = #{passwd}
